create table tb_user (user_id char(36) not null, email varchar(255) not null, password varchar(255) not null, constraint pk_users primary key (user_id), constraint unq_email unique (email));
create table tb_message (message_id char(36) not null, author char(36) not null, content varchar(255) not null, sent_at bigint not null, constraint pk_message primary key (message_id), constraint fk_message_author foreign key (author) references tb_user(user_id));
create table tb_group (group_id char(36) not null, owner char(36) not null, name varchar(32) not null, created_at bigint not null, constraint pk_group primary key (group_id), constraint fk_group_owner foreign key (owner) references tb_user(user_id));
create table tb_group_participant (group_participant_id identity primary key, group_id char(36) not null, user_id char(36) not null, constraint fk_group_participant_group foreign key (group_id) references tb_group(group_id), constraint fk_group_participant_user foreign key (user_id) references tb_user(user_id));
create table tb_group_message (group_message_id identity primary key, group_id char(36) not null, sender_id char(36) not null, content varchar(255) not null, sent_at bigint not null, constraint fk_group_message_group foreign key (group_id) references tb_group(group_id), constraint fk_group_message_sender foreign key (sender_id) references tb_user(user_id));
